LOCK TABLES `newsarchive` WRITE;
/*!40000 ALTER TABLE `newsarchive` DISABLE KEYS */;
INSERT INTO `newsarchive` VALUES (14,'小倩','長達 50 分鐘！睽違 530 天的 Nintendo Direct 將於 2 月 18 日播出','<p style=\"margin-left:0px;\">　　任天堂宣布，將於台灣時間 2 月 18 日早上 6 點播出最新一期的預錄直播節目「Nintendo Direct 2021.2.18」，介紹預定於 2021 上半年發售的 Nintendo Switch 軟體，以及《<a href=\"https://acg.gamer.com.tw/search.php?encode=utf8&amp;kw=%E4%BB%BB%E5%A4%A9%E5%A0%82%E6%98%8E%E6%98%9F%E5%A4%A7%E4%BA%82%E9%AC%A5+%E7%89%B9%E5%88%A5%E7%89%88\">任天堂明星大亂鬥 特別版</a>》最新消息等情報，影片長度約 50 分鐘。</p><figure class=\"image\"><img src=\"https://p2.bahamut.com.tw/B/2KU/86/d5585ff4472ba064169dd0b3bb1bhk25.JPG?v=1613533329020\" alt=\"image\" srcset=\"https://p2.bahamut.com.tw/B/2KU/86/d5585ff4472ba064169dd0b3bb1bhk25.JPG?w=1000 1x,https://p2.bahamut.com.tw/B/2KU/86/d5585ff4472ba064169dd0b3bb1bhk25.JPG 2x\" sizes=\"100vw\"></figure><p style=\"margin-left:0px;\">　　根據粉絲非官方統計，這次的 Nintendo Direct 距離上一次正式的 Nintendo Direct（Nintendo Direct 2019.9.5）已相隔了 530 天的時間，距離上一次小型的 Nintendo Direct Mini （Nintendo Direct mini 2020.10）也已經過了 110 天。</p><figure class=\"media\"><div data-oembed-url=\"https://youtu.be/tezuZUTyqJw\"><div><iframe src=\"https://www.youtube.com/embed/tezuZUTyqJw\" style=\"width: 801px; height: 450.6px; top: 0; left: 0;\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen=\"\"></iframe></div></div></figure>','TV','/images/news/newsCover/b555fe2f9add846a0c9a63f1271bhk351613572840830.jfif','2021-02-17 22:40:41');
/*!40000 ALTER TABLE `newsarchive` ENABLE KEYS */;
UNLOCK TABLES;

